package com.example.lab6android.model

import com.github.javafaker.Faker

typealias SchedulesListener = (schedules: List<Schedule>) -> Unit

class ScheduleService {

    private var schedules = mutableListOf<Schedule>()
    private val listeners = mutableListOf<SchedulesListener>()

    init {
        val faker = Faker.instance()
        IMAGES.shuffle()
        val cars = (1..10).map {
            Car(
                id = it.toLong(),
                photo = IMAGES[it % IMAGES.size],
                name = "Bus $it",
                carNumber = faker.number().numberBetween(100, 1000).toString() + "ABC",
                carCapacity = faker.number().numberBetween(10, 50)
            )
        }
        schedules = (0..100).map {
            val randomNum = faker.number().randomDigit()
            Schedule(
                id = it.toLong(),
                car = cars[it % cars.size],
                route = faker.address().cityName() + "  ->  " + faker.address().cityName(),
                departDate = "${randomNum%18+10}.${randomNum%12}.2022",
                departTime = "${randomNum%14+10}:${randomNum%50+10}",
                arriveDate = "${(randomNum+faker.number().numberBetween(1, 4))%18+10}.${(randomNum%12)}.2022",
                arriveTime = "${(randomNum+faker.number().numberBetween(1, 10))%14+10}:${randomNum%50+10}"
            ) }.toMutableList()
    }

    fun getSchedules(): List<Schedule> {
        return schedules
    }

    fun deleteSchedule(schedule: Schedule) {
        val indexToDelete = schedules.indexOfFirst { it.id == schedule.id }
        if (indexToDelete != -1) {
            schedules.removeAt(indexToDelete)
        }
        notifyChanges()
    }

    fun addListener(listener: SchedulesListener) {
        listeners.add(listener)
        listener.invoke(schedules)
    }

    fun remove(listener: SchedulesListener) {
        listeners.remove(listener)
    }

    private fun notifyChanges() {
        listeners.forEach { it.invoke(schedules) }
    }

    companion object {
        private val IMAGES = mutableListOf(
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSm0Ht2Ckh3COgeURFj1jinGwp_QyJCuGtw1w&usqp=CAU",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSUPGRyiIOnx1Ak-XAiX9w-_S4LhzW5oPK8-g&usqp=CAU"
        )
    }
}